mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM account
WHERE user_email = #{userEmail, jdbcType=VARCHAR}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM account
WHERE account_number = #{accountNumber, jdbcType=VARCHAR}

mapper.update:
UPDATE account
SET balance = #{balance}
WHERE account_id = #{accountId}

mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM account
WHERE account_id = #{accountId}
AND account_password = #{password,
jdbcType=VARCHAR}
